import React, { useEffect, useState } from "react";
import NewsItems from "./NewsItems";
import PropTypes from "prop-types";
const News = (props) => {
  const [articles, setarticles] = useState([]);

  const [page, setPage] = useState(1);
  const [totalResults, setTotalResults] = useState(0);

  const updateNews = async () => {
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=apiKey=786e9c1383ba4157b5f746da5b9b693b&page=${page}&pageSize=4`;
    setloading(true);
    let data = await fetch(url);
    let parsedData = await data.json();
    setarticles(parsedData.articles);
    setTotalResults(parsedData.totalresults);
    setloading(false);
  };

  useEffect(() => {
    updateNews();
  });

  const handlePrevClick = async () => {
    setPage(page - 1);
    updateNews();
  };

  const handleNextClick = async () => {
    setPage(page + 1);
    updateNews();
  };
  return (
    <div className="container md-3">
      <div className="row">
        {articles.map((element) => {
          return (
            <div className="col-md-4" key={element.url}>
              <NewsItems
                title={element.title?.slice(0, 45)}
                description={element.description?.slice(0, 88)}
                imageurl={element.urlToImage}
                newsurl={element.url}
              />
            </div>
          );
        })}
      </div>
      <div className="container d-flex justify-content-between">
        <button
          disabled={setPage <= 1}
          type="button"
          className="btn btn-dark my-2"
          onClick={handlePrevClick}
        >
          &larr; Previous
        </button>
        <button
          disabled={
            setPage + 1 > Math.ceil(totalResults / props.pagesize)
          }
          type="button"
          className="btn btn-dark my-2"
          onClick={handleNextClick}
        >
          Next &rarr;
        </button>
      </div>
    </div>
  );
};
News.defaultProps = {
  country: "in",
  pagesize: 8,
  category: "general",
};
News.propTypes = {
  country: PropTypes.string,
  pagesize: PropTypes.number,
  category: PropTypes.string,
};
export default News;
